import { Component, OnInit } from '@angular/core';
import { App } from '../_models/app';
import { UserService } from '../_services/user.service';
import { HttpEventType, HttpResponse } from '@angular/common/http';

@Component({
  selector: 'app-userappdetails',
  templateUrl: './userappdetails.component.html'
})
export class UserappdetailsComponent implements OnInit {
  apps?: App[];
  constructor(private userService: UserService) { }

  ngOnInit() {
    this.userService.getAll()
           // .pipe(first())
            .subscribe(apps => this.apps = apps);
  }

  public download(files : string) {
    debugger;
    this.userService.downloadFile(files).subscribe(async (event) => {
        let data = event as HttpResponse < Blob > ;
        const downloadedFile = new Blob([data.body as BlobPart], {
            type: data.body?.type
        });
        console.log("ddd", downloadedFile)
        if (downloadedFile.type != "") {
            const a = document.createElement('a');
            a.setAttribute('style', 'display:none;');
            document.body.appendChild(a);
            a.download = files;
            a.href = URL.createObjectURL(downloadedFile);
            a.target = '_blank';
            a.click();
            document.body.removeChild(a);
        }
    });


  }

}
